.header {
  min-width: 100%;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  overflow: hidden;
  background: $white;
  position: fixed;
  top: 0;
  z-index: 100;
  left: -1000;
  @include respond-to(map-get($bp, l)) {
    position: initial;
    min-height: 20%;
    flex-direction: initial;
    left: 0;
  }
  &__logo {
    width: 40%;
    margin: 10px;
    @include respond-to(map-get($bp, m)) {
      width: 30%;
    }
    @include respond-to(map-get($bp, l)) {
      width: 7%;
    }
    @include respond-to(map-get($bp, xl)) {
      width: 6%;
    }
    & img {
      width: 100%;
    }
  }
  &__nav {
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 10px;
    &__menu {
      min-width: 100%;
      display: flex;
      flex-direction: column;
      align-items: center;
      @include respond-to(map-get($bp, l)) {
        flex-direction: initial;
      }

      &__item {
        text-decoration: none;
        color: $black;
        font-family: $principalFont;
        font-size: fs(m);
        font-weight: 900;
        margin: 10px;
        list-style: none;
        transition: transform 0.3s ease-in-out;
        @include respond-to(map-get($bp, m)) {
          font-size: fs(l);
          margin: 30px;
        }
        @include respond-to(map-get($bp, l)) {
          font-size: fs(m);
          margin: 10px;
        }
        &:hover {
          transform: scale(1.1);
          color: $darkGray;
        }
      }
    }
  }
  &__button {
    @extend %transparent-btn;
    @include respond-to(map-get($bp, m)) {
      width: 330px;
      height: 80px;
      font-size: fs(l);
    }
    @include respond-to(map-get($bp, l)) {
      width: initial;
      height: initial;
      font-size: fs(m);
    }
    transition: all 0.3s ease-in-out;
    &:hover {
      background-color: $secondary;
      color: $white;
    }
  }
}

